const modalLink=document.querySelector(".btn-write-us"),modal=document.querySelector(".modal"),modalClose=document.querySelector(".modal-close"),modalLogin=document.querySelector(".modal-login"),modalPassword=document.querySelector(".modal-pass"),modalText=document.querySelector(".modal-text"),modalForm=document.querySelector(".modal-form");modalLink.addEventListener("click",function(o){o.preventDefault(),modal.classList.add("modal-show"),modalLogin.focus()}),modalClose.addEventListener("click",function(o){o.preventDefault(),modal.classList.remove("modal-show"),modal.classList.remove("modal-error")}),modalForm.addEventListener("submit",function(o){modalLogin.value&&modalPassword.value&&modalText.value||(o.preventDefault(),modal.classList.remove("modal-error"),modal.offsetWidth=modal.offsetWidth,modal.classList.add("modal-error"))}),window.addEventListener("keydown",function(o){27===o.keyCode&&modal.classList.contains("modal-show")&&(o.preventDefault(),modal.classList.remove("modal-show"),modal.classList.remove("modal-error"))});
